import 'dart:async';
import 'dart:convert';
import 'dart:math';
import 'dart:typed_data';

import 'package:flutter/material.dart';

import 'src/footer/load_indicator.dart';
import 'src/footer/footer.dart';

/// 金色校园Footer
class PhoenixFooter extends Footer {
  /// Key
  final Key? key;

  final LinkFooterNotifier linkNotifier = LinkFooterNotifier();

  PhoenixFooter({
    this.key,
    bool enableHapticFeedback = false,
  }) : super(
          extent: 80.0,
          triggerDistance: 80.0,
          float: false,
          enableHapticFeedback: enableHapticFeedback,
          enableInfiniteLoad: false,
          completeDuration: const Duration(seconds: 1),
        );

  @override
  Widget contentBuilder(
      BuildContext context,
      LoadMode loadState,
      double pulledExtent,
      double loadTriggerPullDistance,
      double loadIndicatorExtent,
      AxisDirection axisDirection,
      bool float,
      Duration? completeDuration,
      bool enableInfiniteLoad,
      bool success,
      bool noMore) {
    // 不能为水平方向以及反向
    assert(axisDirection == AxisDirection.down,
        'Widget can only be vertical and cannot be reversed');
    linkNotifier.contentBuilder(
        context,
        loadState,
        pulledExtent,
        loadTriggerPullDistance,
        loadIndicatorExtent,
        axisDirection,
        float,
        completeDuration,
        enableInfiniteLoad,
        success,
        noMore);
    return PhoenixFooterWidget(
      key: key,
      linkNotifier: linkNotifier,
    );
  }
}

/// 金色校园组件
class PhoenixFooterWidget extends StatefulWidget {
  final LinkFooterNotifier linkNotifier;

  const PhoenixFooterWidget({
    Key? key,
    required this.linkNotifier,
  }) : super(key: key);

  @override
  PhoenixFooterWidgetState createState() {
    return PhoenixFooterWidgetState();
  }
}

class PhoenixFooterWidgetState extends State<PhoenixFooterWidget> {
  LoadMode get _loadState => widget.linkNotifier.loadState;

  double get _pulledExtent => widget.linkNotifier.pulledExtent;

  double get _indicatorExtent => widget.linkNotifier.loadIndicatorExtent;

  bool get _noMore => widget.linkNotifier.noMore;

  // 图片资源
  String _buildingsBase64 =
      "";
  String _sunBase64 =
      "";
  String _skyBase64 =
      "";
  late Uint8List _buildingsBytes;
  late Uint8List _sunBytes;
  late Uint8List _skyBytes;

  // 太阳旋转值
  double _sunRotateValue = 0.0;

  // 旋转计时器
  Timer? _sunRotateTimer;

  // 是否旋转太阳
  bool _isRotateSun = false;

  set isRotateSun(bool value) {
    if (_isRotateSun != value) {
      _isRotateSun = value;
      if (_isRotateSun) {
        _sunRotateValue = _pulledExtent;
        rotateSun();
      } else {
        if (_sunRotateTimer != null) {
          _sunRotateTimer!.cancel();
        }
      }
    }
  }

  @override
  void initState() {
    super.initState();
    _buildingsBytes = base64Decode(_buildingsBase64);
    _sunBytes = base64Decode(_sunBase64);
    _skyBytes = base64Decode(_skyBase64);
    _sunRotateValue = _pulledExtent;
  }

  @override
  void dispose() {
    if (_sunRotateTimer != null && _sunRotateTimer!.isActive) {
      _sunRotateTimer!.cancel();
    }
    super.dispose();
  }

  // 旋转太阳
  void rotateSun() {
    _sunRotateTimer = Timer(Duration(milliseconds: 10), () {
      setState(() {
        _sunRotateValue += 2;
        rotateSun();
      });
    });
  }

  @override
  Widget build(BuildContext context) {
    if (_noMore) return Container();
    if (_loadState == LoadMode.armed || _loadState == LoadMode.load) {
      isRotateSun = true;
    } else if (_loadState == LoadMode.done || _loadState == LoadMode.inactive) {
      isRotateSun = false;
    }
    return Stack(
      children: <Widget>[
        Positioned(
          bottom: 0.0,
          left: 0.0,
          right: 0.0,
          child: Container(
            width: double.infinity,
            height: _pulledExtent > _indicatorExtent
                ? _pulledExtent
                : _indicatorExtent,
            child: Stack(
              children: <Widget>[
                // 天空
                Container(
                  width: double.infinity,
                  height: double.infinity,
                  color: Colors.blue,
                ),
                // 云
                Positioned(
                  top: 0.0,
                  left: 0.0,
                  right: 0.0,
                  child: Container(
                    width: double.infinity,
                    height: _indicatorExtent,
                    child: Image.memory(
                      _skyBytes,
                      fit: BoxFit.cover,
                    ),
                  ),
                ),
                // 学校
                Positioned(
                  top: 0.0,
                  left: 0.0,
                  right: 0.0,
                  child: Container(
                    margin: EdgeInsets.only(top: 15.0),
                    width: double.infinity,
                    height: _pulledExtent < _indicatorExtent
                        ? _indicatorExtent
                        : _pulledExtent,
                    child: Image.memory(
                      _buildingsBytes,
                      fit: BoxFit.fitHeight,
                    ),
                  ),
                ),
                // 太阳
                Positioned(
                  top: 0.0,
                  left: 0.0,
                  right: 0.0,
                  child: Container(
                    margin: EdgeInsets.only(
                        top: _pulledExtent < _indicatorExtent - 20.0
                            ? _indicatorExtent / 2 - _pulledExtent / 2
                            : 10.0),
                    width: double.infinity,
                    height: _indicatorExtent,
                    child: Center(
                      child: Container(
                        height: _indicatorExtent,
                        width: 110.0,
                        child: Align(
                          alignment: Alignment.topLeft,
                          child: Transform.rotate(
                            child: Container(
                              height: 30.0,
                              width: 30.0,
                              child: Image.memory(
                                _sunBytes,
                                fit: BoxFit.fill,
                              ),
                            ),
                            angle: (_isRotateSun
                                    ? _sunRotateValue
                                    : _pulledExtent) /
                                8 /
                                pi,
                          ),
                        ),
                      ),
                    ),
                  ),
                ),
              ],
            ),
          ),
        ),
      ],
    );
  }
}
